mapper.select:
select * from t_guild_daily_statics where user_id = #{userId}
ORDER BY id desc

    limit #{start}, #{limit}

mapper.insert:
insert into t_guild_daily_statics ( ymd, user_id, guild_id, number, revenue, expend ) values
( #{item.ymd}, #{item.userId}, #{item.guildId}, #{item.number}, #{item.revenue}, #{item.expend}
)
