mapper.select:
	    select * from t_version 


     		 and description= #{description}


     		 and versionName= #{versionName}


     		 and versionNo= #{versionNo}


     		 and updateUrl= #{updateUrl}


     		 and fc= #{fc}


     		 and updateTime= #{updateTime}


     		 and `release` = #{release}


     		 and type=#{type}
